import {useEffect, useState} from 'react'
import {useParams} from 'react-router-dom'

const EntryDetail = () => {

    const {entryId} = useParams();
    const [entry, setEntry] = useState(null)
    const [editMode, setEditMode] = useState(false);
    const [editedBody, setEditedBody] = useState("")
    const [title, setTitle] = useState('')
    const [body, setBody] = useState('')
    const [error, setError] = useState(null)


    useEffect(() => {
        const fetchEntry = async () => {
            const response = await fetch(`/entries/${entryId}`)
            const json = await response.json()
            if (response.ok) {
                setEntry(json)
                setEditedBody(json.body)
                setTitle(json.title)
            }
        }
        fetchEntry()
    }, [entryId])
    
    const handleClick =  () => {
        setEditMode(!editMode);
    }

    const handleSave = async (entry) => {
        //api call to update entry
        const newEntry = {title, editedBody}
        const response = await fetch('/entries/' + entry._id, {
            method: 'PATCH',
            body: JSON.stringify(newEntry),
            headers: {
                'Content-Type': 'application/json'
            }
        })
        const json = await response.json()

        if (!response.ok) {
            setError(json.error)
        }
        setEditMode(false)
        
    }

    return (
        <div className="entry-detail">
            {entry && (
                <div key={entry._id}>
                    <h1>{entry.title}</h1>
                    <span className="edit-button" onClick={() => handleClick(entry)}>
                        <i className="material-icons">edit</i> {/* Material Icons trash can icon */}
                    </span>
                    {editMode ? (
                        <div>
                            <textarea
                                value={editedBody}
                                onChange={(e) => setEditedBody(e.target.value)}
                            />
                            <button onClick={() => handleSave(entry)}>Save</button>
                        </div>
                    ) : (
                        <p>{entry.body}</p>
                    )}
                    
                </div>
            )}
            {error && <div className="error">{error}</div>}
        </div>
    )
}

export default EntryDetail
