import {useEffect, useState} from 'react'
import {useParams} from 'react-router-dom'
import { useEntriesContext } from '../hooks/useEntriesContext'

const EntryDetail = () => {

    const {entryId} = useParams();
    const [entry, setEntry] = useState(null)
    const [editMode, setEditMode] = useState(false);
    const [editedBody, setEditedBody] = useState("")
    const [error, setError] = useState(null)

    const {entries, dispatch} = useEntriesContext()


    useEffect(() => {
        const fetchEntry = async () => {
            const response = await fetch(`/entries/${entryId}`)
            const json = await response.json()
            if (response.ok) {
                setEntry(json)
            }
        }
        fetchEntry()
        if (entry) {
            setEditedBody(entry.body)
        }
    }, [entryId])
    
    const handleClick =  () => {
        setEditMode(!editMode);
    }

    const handleSave = async (entry) => {
        //api call to update entry
        const newEntry = {editedBody}
        const response = await fetch('/entries/' + entry._id, {
            method: 'PATCH',
            body: JSON.stringify({ body: editedBody }),
            headers: {
                'Content-Type': 'application/json'
            }
        })
        console.log(JSON.stringify(newEntry))
        const json = await response.json()

        if (!response.ok) {
            setError(json.error)
        }
        setEditMode(false)
        
    }

    return (
        <div className="entry-detail">
            {entry && (
                <div key={entry._id}>
                    <h1>{entry.title}</h1>
                    <span className="edit-button" onClick={() => handleClick(entry)}>
                        <i className="material-icons">edit</i> {/* Material Icons trash can icon */}
                    </span>
                    {editMode ? (
                        <div>
                            <textarea
                                value={editedBody}
                                onChange={(e) => setEditedBody(e.target.value)}
                            />
                            <button onClick={() => handleSave(entry)}>Save</button>
                        </div>
                    ) : (
                        <p>{entry.body}</p>
                    )}
                    
                </div>
            )}
            {error && <div className="error">{error}</div>}
        </div>
    )
}

export default EntryDetail
