import {useEffect, useState} from 'react'
import {useParams} from 'react-router-dom'

const EntryDetail = () => {

    const {entryId} = useParams();
    const [entry, setEntry] = useState(null)
    const [editMode, setEditMode] = useState(false);
    const [editedBody, setEditedBody] = useState("")


    useEffect(() => {
        const fetchEntry = async () => {
            const response = await fetch(`/entries/${entryId}`)
            const json = await response.json()
            if (response.ok) {
                setEntry(json)
            }
            if (entry) {
                setEditedBody(entry.body)
            }
        }
        fetchEntry()
    }, [entryId])

    const handleClick =  () => {
        setEditMode(!editMode);
    }

    const handleSave = async (entry) => {
        //api call to update entry

        setEditMode(false);
    }

    return (
        <div className="entry-detail">
            {entry && (
                <div key={entry._id}>
                    <h1>{entry.title}</h1>
                    <span className="edit-button" onClick={() => handleClick(entry)}>
                        <i className="material-icons">edit</i> {/* Material Icons trash can icon */}
                    </span>
                    {editMode ? (
                        <div>
                            <textarea
                                value={editedBody}
                                onChange={(e) => setEditedBody(e.target.value)}
                            />
                            <button onClick={handleSave}>Save</button>
                        </div>
                    ) : (
                        <p>{entry.body}</p>
                    )}
                    
                </div>
            )}
        </div>
    )
}

export default EntryDetail
